import "./App.css";
import CardColumns from "react-bootstrap/CardColumns";
import CardComponent from "./components/CardComponent";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import React, { useEffect, useState } from "react";
import Row from "react-bootstrap/Row";

function Proyectos() {
  const [projects, setProjects] = useState(null);

  useEffect(() => {
    const fetchProjects = async () => {
      /* code needed later to fetch data needed for the page
      const { data } = await axios(`${URI}/ruta/del/endpoint`);
      const { totalPages, page } = data;
      const projectList = data.projects;
      */

      // mock data for the time being
      let projectList = [
        {
          title: "10K Challenge",
          description: "10K Description",
          route: "#10KChallenge",
        },
      ];

      setProjects(projectList);
    };

    fetchProjects();
  }, []);

  return (
    <div>
      <Container fluid className="px-5 pb-5">
        <h1>Proyectos</h1>
        <Row>
          <Col>
            <CardColumns style={{columnCount: 5}}>
              {projects
                ? projects.map((project, i) => (
                    <CardComponent
                      title={project.title}
                      description={project.description}
                      route={project.route}
                    />
                  ))
                : ""}
            </CardColumns>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Proyectos;
