import React, { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import CardRow from "./components/CardRow";
import "./App.css";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

function Proyectos() {
  const [projects, setProjects] = useState([]);

  const splitProjects = ( projectList ) => {
    const projectChunks = [];
    while (projectList.length) {
      projectChunks.push(projectList.splice(0, 5));
    }

    setProjects(projectChunks);
  };

  useEffect(() => {
    const fetchProjects = async () => {
      /* insert fetching code later */

      // split projects into chunks of 5 to map in the card rows
      let projectChunks = [
        [
          {
            title: "10K Challenge",
            description: "10K Description",
            route: "#10KChallenge",
          },
          {
            title: "Universities for Founders MTY",
            description: "Universities for Founders Description",
            route: "#UniversitiesForFounders",
          },
        ],
      ];

      while (projects.length) {
        projectChunks.push(projects.splice(0, 5));
      }

      setProjects(projectChunks);
    };

    fetchProjects();
  }, []);

  return (
    <div>
      <Container fluid className="px-5">
        <h1>Proyectos</h1>
        <Row>
          <Col></Col>
        </Row>
      </Container>
    </div>
  );
}

export default Proyectos;
