import "../App.css";
import React from "react";
import Jumbotron from "react-bootstrap/Jumbotron";

function ProjectBanner({ title, imgUrl, author }) {
  return (
    <Jumbotron
      fluid
      className="d-flex align-items-end bg-cover"
      style={{
        position: "relative",
        backgroundImage: `url(${imgUrl})`,
        height: 400,
      }}
    >
      <div className="px-5 py-4 text-white" style={{ backgroundColor: "black" }}>
        <h1  style={{ fontSize: "2em" }}>
          {title}
        </h1>
        <h5>Encargado: {author}</h5>
      </div>
    </Jumbotron>
  );
}

export default ProjectBanner;
